import React, { useState } from "react";
import { getCartItems } from "../../functions/functions";
import "./style.css";

export default function OrderSummaryBox({ user }) {
  const [itemList, setItemList] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const [tax, setTax] = useState(0);
  const navigate = useNavigate();

  const getItemList = async () => {
    const data = await getCartItems();
    var tmp_subtotal = 0;

    if (itemList.length === 0) {
      setItemList(data);
      data.map((item, index) => {
        tmp_subtotal += item.quantity * item.price;
      });
      setSubtotal(tmp_subtotal);
      setTax(Math.round(tmp_subtotal * 0.075 * 100) / 100);
    }
  };

  getItemList();
  return (
    <div className="summary_box">
      <div className="summary_content">
        <span>Order Summary</span>
        <div className="line" style={{ opacity: 0.5 }}></div>
      </div>
    </div>
  );
}
