import React, { useState } from "react";
import CountrySelector from "../../Components/country dropdown";
import Header from "../../Components/header";
import InputField from "../../Components/input_field";
import PolicyBox from "../../Components/policy_box";
import "./style.css";

export default function SignUp() {
  const signin_info = {
    email: "",
    password: "",
  };
  const personalInfo = {
    first_name: "",
    last_name: "",
    email: "",
    address_1: "",
    address_2: "",
    country: "",
    region: "",
    city: "",
    zip: "",
    phone: "",
    company: "",
    shipping_method: "",
    payment_method: null,
  };

  const [userEnter, setUserEnter] = useState(personalInfo);
  const [signinInfo, setSigninInfo] = useState(signin_info);

  function onChangePersonalInfo(e) {
    const { name, value } = e.target;

    setUserEnter({ ...userEnter, [name]: value });
  }
  function onChangeSigninInfo(e) {
    const { name, value } = e.target;
    if (name === "email") {
      setUserEnter({ ...userEnter, [name]: value });
    }
    setSigninInfo({ ...signinInfo, [name]: value });
  }
  console.log(userEnter);

  return (
    <div>
      <Header />
      <div className="signup_wrapper">
        <span className="signup_title">Create New Account</span>
        <div style={{ marginBottom: "2rem" }} className="line"></div>
        <span className="signup_subtitle">Sign In Info</span>
        <InputField
          label="Email"
          name="email"
          require={true}
          onChange={onChangeSigninInfo}
        />
        <InputField
          label="Password"
          name="password"
          require={true}
          type="password"
          onChange={onChangeSigninInfo}
        />
        <span style={{ marginTop: "3rem" }} className="signup_subtitle">
          Shipping Info
        </span>
        <div style={{ display: "flex" }}>
          <InputField
            label="First Name"
            name="first_name"
            require={true}
            onChange={onChangePersonalInfo}
          />
          <InputField
            label="Last Name"
            name="last_name"
            require={true}
            onChange={onChangePersonalInfo}
          />
        </div>
        <InputField label="Company" name="company" require={false} />
        <div style={{ display: "flex" }}>
          <InputField
            label="Address 1"
            name="address_1"
            require={true}
            onChange={onChangePersonalInfo}
          />
          <InputField
            label="Address 2"
            name="address_2"
            require={false}
            onChange={onChangePersonalInfo}
          />
        </div>

        <div style={{ marginTop: "1.5rem" }}>
          <CountrySelector setUserEnter={setUserEnter} />
        </div>
        <InputField
          label="City"
          name="city"
          require={true}
          onChange={onChangePersonalInfo}
        />
        <InputField
          label="Zip"
          name="zip"
          require={true}
          onChange={onChangePersonalInfo}
        />
        <InputField
          label="Phone Number"
          name="phone"
          require={true}
          onChange={onChangePersonalInfo}
        />

        <PolicyBox />
        <button style={{ width: "15%" }} className="black_long_btn">
          Sign Up
        </button>
      </div>
    </div>
  );
}

