import React, { useState } from "react";
import Header from "../../Components/header";
import "./style.css";

export default function Item() {
  const mainUrl =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_1_450x.png?v=1609399094";
  const subUrl0 = mainUrl;
  const subUrl1 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_06_450x.jpg?v=1609399094";
  const subUrl2 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_03_450x.jpg?v=1609399094";
  const subUrl3 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_04_450x.jpg?v=1609399094";

  const [mainImg, setMainImg] = useState(mainUrl);
  const [optionVisible, setOptionVisible] = useState(false);

  const images = [subUrl0, subUrl1, subUrl2, subUrl3];

  const price0 = {
    ml: "100ml",
    price: "$990",
  };
  const [mainPrice, setMainPrice] = useState(price0);

  const prices = [
    {
      ml: "100ml",
      price: "$990",
    },
    {
      ml: "150ml",
      price: "$1499",
    },
    {
      ml: "200ml",
      price: "$1799",
    },
    {
      ml: "250ml",
      price: "$2099",
    },
  ];
  return (
    <div>
      <Header />
      <div className="row">
        <div className="col_md">
          <div className="product_images">
            <img
              className="product_image  product-single__thumbnail-image"
              src={mainImg}
              alt="main image"
            />
          </div>
          <div className="other_images">
            {images.map((img, i) => (
              <img
                key={i}
                src={img}
                alt=""
                style={{ cursor: "pointer" }}
                onClick={() => {
                  setMainImg(img);
                }}
              />
            ))}
          </div>
        </div>

        <div className="col_md" style={{ "padding-top": "2em" }}>
          <div className="product_header">
            <h1>Sauvage</h1>
            <h2>Eau de toilette</h2>
          </div>
          <div className="form">
            <div className="option_box hover1">
              <button
                onClick={() =>
                  !optionVisible
                    ? setOptionVisible(true)
                    : setOptionVisible(false)
                }
              >
                <div style={{ width: "100%" }}>
                  <span style={{ float: "left" }}>{mainPrice.ml}</span>
                  <span style={{ float: "right" }}>{mainPrice.price}</span>
                </div>
              </button>
            </div>

            {optionVisible && (
              <div className="other_option_box">
                {prices.slice(1, prices.length).map((item, i) => (
                  <div
                    className="option hover1"
                    style={{ width: "100%", padding: "10px" }}
                    onClick={() => {
                      setMainPrice(item);
                    }}
                  >
                    <span style={{ float: "left" }}>{item.ml}</span>
                    <span style={{ float: "right" }}>{item.price}</span>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
