import React from "react";
import Card from "react-bootstrap/Card";
import { titleFormat } from "../../functions/functions";
import "./style.css";

export default function CardComp({ title, image, price, name }) {
  return (
    <div className="single_card">
      <Card border="dark" style={{ width: "15rem" }}>
        <div className="thumbnail">
          <Card.Img variant="top" src={image} />
        </div>

        <Card.Body>
          <Card.Title>
            <span className="title">{titleFormat(title)}</span>
          </Card.Title>
          <Card.Text>
            <div className="price_tag">
              <span>
                {name !== 0 ? <span>{name} ml</span> : <span>{name}</span>}
              </span>
              <span>${price}</span>
            </div>
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
}
