import axios from "axios";
import React, { useState } from "react";
import "./style.css";

export default function PopUp() {
  const [itemList, setItemList] = useState([]);
  const getItems = async () => {
    axios.get("http://localhost:8001/getCartItemList").then((res) => {
      setItemList(res.data);
    });
  };
  if (itemList.length == 0) {
    getItems();
  }
  return (
    <div className="popup">
      <div className="content">
        <span>Cart</span>
        <div className="cart_item">
          <img
            src="https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_1_450x.png?v=1609399094"
            alt="cart item's image"
          />
          item 1
        </div>
        <div className="cart_item">
          <img
            src="https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_1_450x.png?v=1609399094"
            alt="cart item's image"
          />
          item 1
        </div>
      </div>
    </div>
  );
}
