import axios from "axios";
import React, { useState } from "react";
import "./style.css";

export default function PopUp() {
  const [itemList, setItemList] = useState([]);
  const [total, setTotal] = useState(0);

  const getItems = async () => {
    axios.get("http://localhost:8001/getCartItems").then((res) => {
      setItemList(res.data);
    });
  };
  if (itemList.length === 0) {
    getItems();
  }
  var cumulative = 0;
  if (itemList.length !== 0 && total === 0) {
    itemList.map((item, index) => {
      cumulative += item.quantity * item.price;
    });
    setTotal(cumulative);
  }

  const [quanList, setQuanList] = useState([]);
  const tmp_quanList = [];
  if (itemList.length !== 0 && quanList.length === 0) {
    itemList.map((item, index) => {
      tmp_quanList.push(item.quantity);
    });
    setQuanList(tmp_quanList);
  }

  const onUpdateSubmit = () => {
    try {
      itemList.map((item, index) => {
        if (itemList.length !== 0 && quanList.length !== 0) {
          axios.post("http://localhost:8001/updateCart", {
            title: item.title,
            newQuan: quanList[index],
          });
        }
      });
    } catch (error) {
      console.log("fail at onUpdateSubmit");
    }
  };

  return (
    <div className="popup_wrapper">
      <div className="popup">
        <span className="cart_title">Cart</span>
        <div className="content">
          {itemList.map((item, index) => (
            <div className="cart_item">
              <img src={item.img} alt="cart item's image" />
              <div className="title_and_option">
                <span style={{ fontWeight: "600", width: "7rem" }}>
                  {item.title}
                </span>
                <span>{item.ml}ml</span>
              </div>
              <span>Qty: </span>

              <input
                id={`quan_control_${index}`}
                type="text"
                className="quan_control"
                value={quanList[index]}
              />
              <div className="add_minus">
                <button
                  className="add_minus_btn"
                  onClick={() => {
                    quanList[index] += 1;
                    setQuanList(quanList);
                  }}
                >
                  +
                </button>
                <button
                  className="add_minus_btn"
                  onClick={() => {
                    if (quanList[index] > 0) {
                      quanList[index] -= 1;
                      setQuanList(quanList);
                    }
                  }}
                >
                  -
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="total_amount">
        <span style={{ fontWeight: "600" }}>Total: </span>
        <span>${total}</span>
      </div>
      <div className="btn_wrap">
        <button
          className="update btn"
          onClick={() => {
            onUpdateSubmit();
          }}
        >
          Update
        </button>
        <button className="checkout btn">Check Out</button>
      </div>
    </div>
  );
}
