import React, { useEffect, useState } from "react";
import CardComp from "../../Components/card/card";
import axios from "axios";
import "./style.css";
import { useNavigate } from "react-router-dom";
import Header from "../../Components/header";
import SlidePhotos from "../../Components/slidePhotos";
import Footer from "../../Components/footer";
import { capitalize } from "lodash";

export default function Product() {
  const navigate = useNavigate();
  const [itemList, setItemList] = useState([]);
  const [categories, setCategories] = useState([]);

  const getByCategory = async (cat) => {
    try {
      const { data } = await axios.post(
        "http://localhost:8001/get-by-category",
        {
          category: cat,
        }
      );
      return data.length;
    } catch (error) {
      console.log("fail at get category");
    }
  };

  const getItems = async () => {
    try {
      const temp_cat = [];
      axios.get("http://localhost:8001/getItemList").then((res) => {
        setItemList(res.data);
        res.data.map((item, index) => {
          if (!temp_cat.includes(item.category)) {
            temp_cat.push(item.category);
            setCategories(temp_cat);
          }
        });
      });
    } catch (error) {}
  };

  if (itemList.length === 0) {
    getItems();
  }

  const [counter, setCounter] = useState([]);
  const temp_counter = [];

  useEffect(() => {
    categories.forEach(async (item) => {
      var obj = {};
      const num = await getByCategory(item);
      obj[item] = num;
      temp_counter.push(obj);
      setCounter(temp_counter);
    });
  });

  console.log(counter);
  return (
    <div className="card_wappper">
      <Header />
      <SlidePhotos />

      <div className="product_container">
        <div className="category_box">
          <div className="category_content">
            <span style={{ fontSize: "22px" }}>Categories</span>
            {categories.map((item, index) => (
              <span>
                {capitalize(item)} <span>{counter[item]}</span>
              </span>
            ))}
          </div>
        </div>
        <div className="product_cards">
          {itemList.map((item, index) => (
            <div
              className="card"
              onClick={() => {
                navigate(`items/${item.title}`);
              }}
            >
              <CardComp
                title={item.title}
                image_1={item.options.images[0]}
                image_2={item.options.images[1]}
                price={item.options.prices[0].price}
                name={item.options.prices[0].ml}
              />
            </div>
          ))}
        </div>
      </div>

      <Footer marginTop="20vh" />
    </div>
  );
}
