import React, { useRef, useState } from "react";
import "./style.css";
import Search from "../../svg/Search";
import Cart from "../../svg/Cart";
import PopUp from "../cartPopup";
import useClickOutside from "../helper/useClickOutside";
import axios from "axios";

export default function Header({ numberOfItems }) {
  const [cartVisible, setCartVisible] = useState(false);
  const cartPopup = useRef(null);
  const [totalQuan, setTotalQuan] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const [result, setResult] = useState([]);
  useClickOutside(cartPopup, () => {
    setCartVisible(false);
  });

  const search = async (searchTerm) => {
    try {
      const { data } = await axios.post(
        `http://localhost:8001/search/${searchTerm}`,
        {}
      );
      return data;
    } catch (error) {
      return error.response.data.message;
    }
  };

  const getItems = async () => {
    try {
      axios.get("http://localhost:8001/getCartItems").then((res) => {
        var updateQuan = 0;
        res.data.map((item, index) => {
          updateQuan += item.quantity;
        });
        setTotalQuan(updateQuan);
      });
    } catch (error) {
      console.log(error.message);
    }
  };
  const searchHandler = async () => {
    if (searchTerm === "") {
      setResult("");
    } else {
      const res = await search(searchTerm);
      setResult(res);
    }
  };
  console.log(result);
  getItems();

  return (
    <div className="header_wapper">
      <div className="header">
        <div className="search">
          <input
            type="text"
            placeholder="Search"
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
            }}
            onKeyUp={searchHandler}
          />
          <button>
            <Search />
          </button>
        </div>
        <div className="header_items">
          <a href="/" className="item hover2">
            Home
          </a>
          <a className="item hover2" href="/">
            Products
          </a>
          <a className="item hover2" href="/">
            Contact
          </a>
          <a className="item hover2" href="/">
            Log In
          </a>
          <a className="item hover2" href="/">
            Sign Up
          </a>
          <div className="cart" ref={cartPopup}>
            <a
              className="item hover2"
              style={{ cursor: "pointer" }}
              onClick={() => {
                if (cartVisible) {
                  setCartVisible(false);
                } else {
                  setCartVisible(true);
                }
              }}
            >
              <div className="numberOfItems">{totalQuan}</div>
              <Cart />
            </a>
            {cartVisible && <PopUp setCartVisible={setCartVisible} />}
          </div>
        </div>
      </div>
    </div>
  );
}
