import React, { useState } from "react";
import { getItems } from "../../backend/controllers/item";
import Header from "../../Components/header";
import { getCartItems, titleFormat } from "../../functions/functions";
import "./style.css";

export default function Checkout() {
  const [itemList, setItemList] = useState([]);

  const getItemList = async () => {
    const data = await getCartItems();
    if (itemList.length === 0) {
      setItemList(data);
    }
  };
  getItemList();
  console.log(itemList);

  return (
    <div className="checkout">
      <Header />
      <div className="content_wrapper">
        <div className="cart_label">Order Summary</div>
        <div className="line"></div>
        <div className="item_field">
          {itemList.map((item, index) => (
            <div className="item_display" key={index}>
              <div className="col_1">
                <img src={item.img} alt="item_display" />
                <div className="item_info">
                  <span className="item_title item_span">
                    {titleFormat(item.title)}
                  </span>
                  <span className="item_span">{item.ml} ml</span>
                  <span className="item_span">Price: ${item.price}</span>
                </div>
              </div>
              <div className="col_2" style={{ fontWeight: "600" }}>
                Quantity:
                <div
                  className="quantity"
                  style={{ paddingLeft: "5px", fontWeight: "500" }}
                >
                  {item.quantity}
                </div>
              </div>
              <div className="col_3" style={{ fontWeight: "600" }}>
                Total:
                <div
                  className="total_price"
                  style={{ paddingLeft: "5px", fontWeight: "500" }}
                >
                  ${item.price * item.quantity}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
