import React, { useState } from "react";
import { useSelector } from "react-redux";
import CountrySelector from "../country dropdown";
import InputField from "../input_field";
import "./style.css";

export default function PersonalInfo({
  setPercent,
  setUserEnter,
  userEnter,
  setPageChange,
}) {
  const { user } = useSelector((user) => ({ ...user }));
  const {
    first_name,
    last_name,
    email,
    company,
    address_1,
    address_2,
    country,
    region,
    city,
    zip,
    phone,
    fax,
    shipping_method,
  } = user.personal_info;

  const [error, setError] = useState(false);
  const handleRegisterChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const errorHandler = () => {
    if (
      first_name === "" ||
      last_name === "" ||
      email === "" ||
      address_1 === "" ||
      country === "" ||
      region === "" ||
      city === "" ||
      zip === "" ||
      phone === "" ||
      shipping_method === ""
    ) {
      setError(true);
      window.scrollTo(0, 0);
    } else {
      setError(false);
      setPercent("50");
      setPageChange(2);
    }
  };

  return (
    <div className="per_info_wrapper">
      <div className="personal_box">
        <div className="per_row_1">
          <span className="ship_label">Shipping Details</span>
          {error && (
            <span style={{ color: "red", margin: "1rem", marginLeft: "0" }}>
              <img
                src="https://cdn-icons-png.flaticon.com/512/463/463612.png"
                alt=""
                style={{
                  width: "20px",
                  height: "20px",
                  transform: `translate(${-3}px, ${3}px)`,
                }}
              />
              Fields with * are required.
            </span>
          )}
          <div className="flex_wrapper">
            <InputField
              label="First Name"
              name="first_name"
              value={first_name}
              onChange={handleRegisterChange}
              require={true}
            />

            <InputField
              label="Last Name"
              name="last_name"
              value={last_name}
              onChange={handleRegisterChange}
              require={true}
            />
          </div>

          <InputField
            label="Email"
            name="email"
            value={email}
            onChange={handleRegisterChange}
            require={true}
          />

          <InputField
            label="Company"
            name="company"
            value={company}
            onChange={handleRegisterChange}
            require={false}
          />

          <div
            className="line"
            style={{ marginTop: "5%", opacity: "0.5" }}
          ></div>

          <div className="flex_wrapper">
            <InputField
              label="Street Address: Line 1"
              name="address_1"
              value={address_1}
              onChange={handleRegisterChange}
              require={true}
            />

            <InputField
              label="Address Line 2"
              name="address_2"
              value={address_2}
              onChange={handleRegisterChange}
              require={false}
            />
          </div>
          <div className="input_field_wrapper">
            <CountrySelector setUser={setUser} user={user} />
          </div>

          <div className="flex_wrapper">
            <InputField
              label="City"
              name="city"
              value={city}
              onChange={handleRegisterChange}
              require={true}
            />

            <InputField
              label="Zip/Postal Code"
              name="zip"
              value={zip}
              onChange={handleRegisterChange}
              require={true}
            />
          </div>

          <InputField
            label="Phone Number"
            name="phone"
            value={phone}
            onChange={handleRegisterChange}
            require={true}
          />

          <InputField
            label="Fax"
            name="fax"
            value={fax}
            onChange={handleRegisterChange}
            require={false}
          />

          <div
            className="line"
            style={{ marginTop: "5%", opacity: "0.5" }}
          ></div>

          <span style={{ marginTop: "3%", marginBottom: "3%" }}>
            Shipping Methods *
          </span>

          <label className="picker">
            <input
              type="radio"
              style={{ marginRight: "20px" }}
              name="shipping_method"
              onChange={handleRegisterChange}
              value="Economy"
            />
            $0.00 Economy - (5-15 days)
          </label>
          <label className="picker">
            <input
              type="radio"
              style={{ marginRight: "20px" }}
              name="shipping_method"
              value="Standard"
              onChange={handleRegisterChange}
            />
            $5.00 Standard - (3-5 days)
          </label>
          <label className="picker">
            <input
              type="radio"
              style={{ marginRight: "20px" }}
              onChange={handleRegisterChange}
              value="Priority"
              name="shipping_method"
            />
            $10.00 Priority - (1-2 days)
          </label>
          <label className="picker">
            <input
              type="radio"
              style={{ marginRight: "20px" }}
              name="shipping_method"
              value="Overnight"
              onChange={handleRegisterChange}
            />
            $20.00 Overnight
          </label>
        </div>
        <button
          className="black_long_btn continue_btn"
          onClick={() => {
            errorHandler();
          }}
        >
          Continue to payment
        </button>
      </div>
    </div>
  );
}
