import axios from "axios";
import React, { useState } from "react";
import "./style.css";

export default function OrderTrackingInput({ banner_url, setSwitchPage }) {
  const [orderNumber, setOrderNumber] = useState("");
  const [email, setEmail] = useState("");
  const [errorVisible, setErrorVisible] = useState(false);
  const [cannotFind, setCannotFind] = useState(false);

  const findOrder = async () => {
    try {
      const { data } = await axios.post("http://localhost:8001/findOrder", {
        email: email,
        order_number: orderNumber,
      });
      console.log(data);
    } catch (error) {
      setErrorVisible(error.response.data.message);
      console.log(error.response.data.message);
      setCannotFind(true);
    }
  };

  const onSubmitCheck = () => {
    if (orderNumber === "" || email === "") {
      setErrorVisible(true);
    } else {
      findOrder();
    }
  };

  return (
    <div>
      {cannotFind && (
        <div className="cannot_find_wrapper">
          <div className="cannot_find_background"></div>
          <div className="error_box">
            <div className="error_box_content">
              <img
                src="https://cdn-icons-png.flaticon.com/512/1828/1828665.png"
                alt=""
                className="cross_close"
                onClick={() => {
                  setCannotFind(false);
                }}
              />
              <img
                src="https://cdn-icons-png.flaticon.com/512/595/595067.png"
                alt=""
                className="error_icon"
              />
              <span style={{ margin: "0.5rem", marginBottom: "0px" }}>
                {errorVisible}
              </span>
              <span style={{ margin: "0.5rem" }}>
                Please double check your infomation.
              </span>
              <button
                className="black_long_btn"
                onClick={() => {
                  setCannotFind(false);
                }}
              >
                Ok
              </button>
            </div>
          </div>
        </div>
      )}

      <img style={{ marginTop: "60px" }} src={banner_url} alt="" />
      <div className="order_wrapper">
        <span style={{ fontSize: "30px" }}>Order Tracking</span>
        <div className="order_line"></div>
        <span style={{ fontSize: "14px", color: "red" }}>* Required</span>

        <div className="order_box">
          {errorVisible && (
            <span style={{ color: "red", fontSize: "17px" }}>
              <img
                src="https://cdn-icons-png.flaticon.com/512/463/463612.png"
                alt=""
                style={{
                  width: "20px",
                  height: "20px",
                  transform: `translate(${-3}px, ${3}px)`,
                }}
              />
              Fields with * are required.
            </span>
          )}
          <div className="order_inputs_container">
            <div className="order_input">
              <span className="input_label">Order Number *</span>
              <input
                type="text"
                className="payment_input_field"
                onChange={(e) => setOrderNumber(e.target.value)}
              />
            </div>
            <div className="order_input">
              <span className="input_label">Email *</span>
              <input
                type="text"
                className="payment_input_field"
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
          </div>
          <span
            style={{
              fontSize: "13px",
              marginTop: "1.5rem",
              marginBottom: "1.5rem",
              cursor: "pointer",
              textDecoration: "underline",
            }}
          >
            Privacy Policy
          </span>

          <button
            style={{ width: "30%" }}
            className="black_long_btn"
            onClick={onSubmitCheck}
          >
            Check Status
          </button>
          <span style={{ fontSize: "14px", padding: "2rem" }}>
            After your order ships, tracking information may take 48 hours to
            appear. But don’t worry! It’s on the way.
          </span>
        </div>
      </div>
    </div>
  );
}
