import axios from "axios";

export const titleFormat = (name) => {
  const result = name.replaceAll("-", " ");
  return result;
};

export const getItems = async () => {
  try {
    const { data } = axios.get("http://localhost:8001/getCartItems");
    return data;
  } catch (error) {
    return error.message;
  }
};
