import React, { useState } from "react";
import Header from "../../Components/header";
import "./style.css";

export default function Item() {
  const subUrl1 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_06_450x.jpg?v=1609399094";
  const subUrl2 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_03_450x.jpg?v=1609399094";
  const subUrl3 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_04_450x.jpg?v=1609399094";
  const mainUrl =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_1_450x.png?v=1609399094";
  const [mainImg, setMainImg] = useState(mainUrl);
  const images = [mainImg, subUrl1, subUrl2, subUrl3];
  return (
    <div>
      <Header />
      <div className="row">
        <div className="col-md">
          <div className="product_images">
            <img
              className="product-image  product-single__thumbnail-image"
              src={mainImg}
              alt="main image"
            />
          </div>
          <div className="other_images">
            {images.map((img, i) => (
              <img
                key={i}
                src={img}
                alt=""
                style={{ cursor: "pointer" }}
                onClick={() => {
                  setMainImg(img);
                }}
              />
            ))}
          </div>
        </div>

        <div className="col_md" style={{ "padding-top": "2em" }}>
          <div className="product_header">
            <h1>Sauvage</h1>
            <h2>Eau de Toi</h2>
          </div>
          <div className="form">
            <ul role="listbox" style={{ width: "100%" }}>
              <li>$960</li>
              <li>$320</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
