import React, { useState } from "react";
import CardComp from "../card/card";
import axios from "axios";
import "./style.css";

const itemInfo = {
  title: "",
  subtitle: "",
  options: {
    images: [],
    prices: [],
  },
  description: "",
};

export default function Cards() {
  const [items, setItems] = useState(itemInfo);
  const { title, subtitle, options, description } = items;
  const titles = ["Boots", "T-Shirt", "Jeans", "Hat", "Jacket"];

  const getItems = async () => {
    axios.get("http://localhost:8001/getItemList").then((res) => {
      setItems(res.data);
    });
  };

  getItems();

  return (
    <div className="card_wappper">
      <div className="cards">
        {items.map((item, index) => (
          <CardComp title={item.title} />
        ))}
      </div>
    </div>
  );
}
