import axios from "axios";
import React, { useState } from "react";
import Header from "../../Components/header";
import InputField from "../../Components/input_field";
import PolicyBox from "../../Components/policy_box";
import PopupAlert from "../../Components/popupAlert";
import Spinner from "../../Components/spinner";
import Cookies from "js-cookie";

import "./style.css";
import { useDispatch } from "react-redux";
export default function SignIn() {
  const [error, setError] = useState(false);
  const [cannotFind, setCannotFind] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  const onSubmitSignIn = async () => {
    try {
      setLoading(true);
      const response = await axios.post("http://localhost:8001/signin", {
        email: email,
        password: password,
      });
      setLoading(false);
      console.log(response.data);
      dispatch({ type: "LOGIN", payload: response.data });
      Cookies.set("user", JSON.stringify(response.data));
    } catch (error) {
      setLoading(false);
      console.log(error.response.data.message);
      setCannotFind(error.response.data.message);
    }
  };

  return (
    <div className="signin_wrapper">
      <Header />
      {loading && (
        <div className="spinner_loader">
          <div className="cannot_find_background"></div>
          <div className="error_box">
            <div className="loader_container">
              <Spinner loading={true} />
            </div>
          </div>
        </div>
      )}
      {cannotFind && (
        <PopupAlert message={cannotFind} setCannotFind={setCannotFind} />
      )}

      <div className="signin_content">
        <div className="signin_col1">
          <span style={{ fontWeight: 600, fontSize: "30px" }}>
            Customer Sign In
          </span>
          <div className="line"></div>
          {error && (
            <span style={{ color: "red", margin: "1rem", marginLeft: "0" }}>
              <img
                src="https://cdn-icons-png.flaticon.com/512/463/463612.png"
                alt=""
                style={{
                  width: "20px",
                  height: "20px",
                  transform: `translate(${-3}px, ${3}px)`,
                }}
              />
              Fields with * are required.
            </span>
          )}
          <span style={{ marginTop: "0.5rem" }}>
            If you have an account, sign in with your email address.
          </span>
          <InputField
            label="Email"
            require={true}
            name="email"
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <InputField
            label="Password"
            require={true}
            name="password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
          <PolicyBox />
          <button
            style={{ width: "10rem" }}
            className="black_long_btn"
            onClick={() => onSubmitSignIn()}
          >
            Sign In
          </button>
          <span style={{ color: "red", fontSize: "14px", marginTop: "1rem" }}>
            * Required Fields
          </span>
        </div>
      </div>
    </div>
  );
}
