import Cookies from "js-cookie";
export function cartReducer(
  state = Cookies.get("user") ? JSON.parse(Cookies.get("user")) : 0,
  action
) {
  switch (action.type) {
    case "LOGIN":
      return action.payload;
    default:
      return state;
  }
}
