import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getCartItems } from "../../functions/functions";
import "./style.css";

export default function OrderSummaryBox({ user }) {
  const [itemList, setItemList] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const [tax, setTax] = useState(0);
  const navigate = useNavigate();
  const shipping = () => {
    if (user.shipping == "economy") {
      return 0;
    } else if (user.shipping == "standard") {
      return 5;
    } else if (user.shipping == "priority") {
      return 10;
    } else {
      return 20;
    }
  };

  const getItemList = async () => {
    const data = await getCartItems();
    var tmp_subtotal = 0;

    if (itemList.length === 0) {
      setItemList(data);
      data.map((item, index) => {
        tmp_subtotal += item.quantity * item.price;
      });
      setSubtotal(tmp_subtotal);
      setTax(Math.round(tmp_subtotal * 0.075 * 100) / 100);
    }
  };
  getItemList();

  return (
    <div className="summary_box">
      <div className="summary_content">
        <span>Order Summary</span>
        <div className="line" style={{ opacity: 0.5 }}></div>
        <div className="summany_line">
          <span style={{ fontSize: "16px", fontWeight: 600 }}>Subtotal: </span>
          <span style={{ fontSize: "16px" }}>${subtotal}</span>
        </div>
        <div className="summany_line">
          <span style={{ fontSize: "16px", fontWeight: 600 }}>Shipping: </span>
          <span style={{ fontSize: "16px" }}>${shipping}</span>
        </div>
      </div>
    </div>
  );
}
