import userEvent from "@testing-library/user-event";
import React from "react";
import "./style.css";

export default function Confirmation({ user }) {
  return (
    <div className="confirm_wrapper">
      <div className="confirm_label">
        <img
          style={{ width: "70px" }}
          src="https://upload.wikimedia.org/wikipedia/commons/5/50/Yes_Check_Circle.svg"
          alt="green check"
        />
        <div className="text_confirm">
          <span style={{ fontSize: "30px", marginLeft: "20px" }}>
            Thank you! Your Order Number is #6268
          </span>
          <span>A confimation has been sent to email {user.email}</span>
        </div>
      </div>
      <div className="confirm_box"></div>
    </div>
  );
}
