export const titleFormat = (name) => {
  const result = name.replaceAll("-", " ");
  return result;
};

export const getCartItems = async () => {
  try {
    const { data } = await axios.get("http://localhost:8001/getCartItems");
    return data;
  } catch (error) {
    console.log(error.message);
  }
};
