import axios from "axios";
import React, { useRef, useState } from "react";
import Search from "../../svg/Search";
import useClickOutside from "../helper/useClickOutside";
import "./style.css";

export default function SearchComp() {
  const [searchTerm, setSearchTerm] = useState("");
  const [result, setResult] = useState([]);
  const [hisSearchVisible, setHisSeachVisible] = useState(false);
  const searchHis = useRef(null);

  useClickOutside(searchHis, () => {
    setHisSeachVisible(false);
  });

  const searchHandler = async () => {
    if (searchTerm === "") {
      setResult("");
    } else {
      const res = await search(searchTerm);
      setResult(res);
    }
  };

  const search = async (searchTerm) => {
    try {
      const { data } = await axios.post(
        `http://localhost:8001/search/${searchTerm}`,
        {}
      );
      return data;
    } catch (error) {
      return error.response.data.message;
    }
  };

  return (
    <div className="search_wrapper">
      <div className="search">
        <div
          className="search_field"
          onClick={() => {
            if (hisSearchVisible) {
              setHisSeachVisible(false);
            } else {
              setHisSeachVisible(true);
            }
          }}
        >
          <input
            type="text"
            placeholder="Search"
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
            }}
            onKeyUp={searchHandler}
          />
        </div>
      </div>
      {hisSearchVisible && <div className="search_history">asdcsd</div>}
    </div>
  );
}
