import React, { useState } from "react";
import CardComp from "../../Components/card/card";
import axios from "axios";
import "./style.css";
import { useNavigate } from "react-router-dom";

export default function Product() {
  const navigate = useNavigate();
  const [itemList, setItemList] = useState([]);

  const getItems = async () => {
    axios.get("http://localhost:8001/getItemList").then((res) => {
      setItemList(res.data);
    });
  };
  if (itemList.length === 0) {
    getItems();
  }

  return (
    <div className="card_wappper">
      <Header />
      <SlidePhotos />
      <div className="cards">
        {itemList.map((item, index) => (
          <div
            className="card"
            onClick={() => {
              navigate(`items/${item.title}`);
            }}
          >
            <CardComp
              title={item.title}
              image_1={item.options.images[0]}
              image_2={item.options.images[1]}
              price={item.options.prices[0].price}
              name={item.options.prices[0].ml}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
