import Cookies from "js-cookie";
export function userReducer(
  state = Cookies.get("cart") ? JSON.parse(Cookies.get("cart")) : null,

  action
) {
  switch (action.type) {
    case "LOGIN":
      return action.payload;
  }
  return state;
}
