import React, { useState } from "react";
import Header from "../../Components/header";
import "./style.css";

export default function Account() {
  const [pickedOption, setPickedOption] = useState(1);
  return (
    <div className="account_wrapper">
      <Header />
      <div className="account_content">
        <div className="account_col1">
          <div className="account_col1_content">
            <div
              className="account_option_wrapper"
              onClick={() => setPickedOption(1)}
            >
              <div className="red_mark"></div>
              <span
                style={{
                  margin: "1rem",
                  fontWeight: `${pickedOption === 1 ? 600 : 500}`,
                }}
              >
                My Account
              </span>{" "}
            </div>

            <span style={{ margin: "0.5rem" }}>Order History</span>
            <span style={{ margin: "0.5rem" }}>Membership</span>
            <span style={{ margin: "0.5rem" }}>Rewards</span>
          </div>
        </div>
      </div>
    </div>
  );
}
