import { ClipLoader } from "react-spinners";
import React from "react";

const Spinner = ({ loading }) => {
  const style = {
    flex: 1,
    marginTop: 240,
    justifyContent: "center",
    alignItems: "center",
  };
  return (
    <div style={style}>
      <ClipLoader color={"#123abc"} loading={loading} />
    </div>
  );
};

export default Spinner;
