import React, { useState } from "react";
import { getItems } from "../../backend/controllers/item";
import Header from "../../Components/header";
import { getCartItems } from "../../functions/functions";
import "./style.css";

export default function Checkout() {
  const [itemList, setItemList] = useState([]);

  const getItemList = async () => {
    const data = await getCartItems();
    if (itemList.length === 0) {
      setItemList(data);
    }
  };
  getItemList();
  console.log(itemList);

  return (
    <div className="checkout">
      <Header />
      <div className="content_wrapper">
        <div className="cart_label">Order Summary</div>
        <div className="line"></div>
        <div className="item_field">
          {itemList.map((item, index) => (
            <div className="item_display" key={index}>
              <img src={item.img} alt="item_display" />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
