import React, { useState } from "react";
import Header from "../../Components/header";
import InputField from "../../Components/input_field";
import PolicyBox from "../../Components/policy_box";
import PopupAlert from "../../Components/popupAlert";
import "./style.css";
export default function SignIn() {
  const [error, setError] = useState(false);
  const [cannotFind, setCannotFind] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  console.log(email);

  return (
    <div className="signin_wrapper">
      <Header message={cannotFind} setCannotFind={setCannotFind} />
      {cannotFind && <PopupAlert />}
      <div className="signin_content">
        <div className="signin_col1">
          <span style={{ fontWeight: 600, fontSize: "30px" }}>
            Customer Sign In
          </span>
          <div className="line"></div>
          {error && (
            <span style={{ color: "red", margin: "1rem", marginLeft: "0" }}>
              <img
                src="https://cdn-icons-png.flaticon.com/512/463/463612.png"
                alt=""
                style={{
                  width: "20px",
                  height: "20px",
                  transform: `translate(${-3}px, ${3}px)`,
                }}
              />
              Fields with * are required.
            </span>
          )}
          <span style={{ marginTop: "0.5rem" }}>
            If you have an account, sign in with your email address.
          </span>
          <InputField
            label="Email"
            require={true}
            name="email"
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
          <InputField
            label="Password"
            require={true}
            name="password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
          <PolicyBox />
          <button style={{ width: "10rem" }} className="black_long_btn">
            Sign In
          </button>
          <span style={{ color: "red", fontSize: "14px", marginTop: "1rem" }}>
            * Required Fields
          </span>
        </div>
      </div>
    </div>
  );
}
