import React, { useEffect, useState } from "react";
import CardComp from "../../Components/card/card";
import axios from "axios";
import "./style.css";
import { useNavigate, useParams } from "react-router-dom";
import Header from "../../Components/header";
import SlidePhotos from "../../Components/slidePhotos";
import Footer from "../../Components/footer";
import { capitalize } from "lodash";

export default function Product() {
  const navigate = useNavigate();
  const [itemList, setItemList] = useState([]);
  const [categories, setCategories] = useState([]);

  const param = useParams().category;

  const getByCategory = async (cat) => {
    try {
      const temp_cat = [];
      const { data } = await axios.post(
        `http://localhost:8001/products/${param}`,
        {
          category: cat,
        }
      );
      setItemList(data);

      data.map((item, index) => {
        temp_cat.push(item.category);
        setCategories(temp_cat);
      });
    } catch (error) {
      console.log("fail at get category");
    }
  };

  const getItems = async () => {
    try {
      const temp_cat = [];
      axios.get("http://localhost:8001/getItemList").then((res) => {
        setItemList(res.data);
        res.data.map((item, index) => {
          temp_cat.push(item.category);
          setCategories(temp_cat);
        });
      });
    } catch (error) {}
  };

  if (itemList.length === 0) {
    getByCategory();
  }

  const result = {};
  categories.forEach((item) => (result[item] = (result[item] || 0) + 1));

  return (
    <div className="card_wappper">
      <Header />
      <SlidePhotos />

      <div className="product_container">
        <div className="filter_boxes">
          <div className="category_box">
            <div className="category_content">
              <span style={{ fontSize: "22px", fontWeight: 600 }}>
                Categories
              </span>
              <div
                className="category_item"
                onClick={() => {
                  navigate(`../products/all`);
                  window.location.reload();
                }}
              >
                <span>All</span>
              </div>
              {[...new Set(categories)].map((item, index) => (
                <div
                  className="category_item"
                  onClick={() => {
                    navigate(`../products/${item}`);
                    window.location.reload();
                  }}
                >
                  <span>
                    {capitalize(item)} <span>({result[item]})</span>
                  </span>
                </div>
              ))}
            </div>
          </div>
          <div className="brand_box"></div>
        </div>

        <div className="product_cards">
          {itemList.map((item, index) => (
            <div
              className="card"
              onClick={() => {
                navigate(`../items/${item.title}`);
              }}
            >
              <CardComp
                title={item.title}
                image_1={item.options.images[0]}
                image_2={item.options.images[1]}
                price={item.options.prices[0].price}
                name={item.options.prices[0].ml}
              />
            </div>
          ))}
        </div>
      </div>

      <Footer marginTop="20vh" />
    </div>
  );
}
