import React, { useState } from "react";
import CardComp from "../card/card";
import axios from "axios";
import "./style.css";

export default function Cards() {
  const [itemList, setItemList] = useState([]);

  const titles = ["Boots", "T-Shirt", "Jeans", "Hat", "Jacket"];

  const getItems = async () => {
    axios.get("http://localhost:8001/getItemList").then((res) => {
      setItemList(res.data);
    });
  };
  getItems();
  return (
    <div className="card_wappper">
      <div className="cards">
        {itemList.map((item, index) => (
          <div className="card_comp">
            <CardComp
              title={item.title}
              image={item.options.images[0]}
              price={item.options.prices[0]}
              name={item.options.names[0]}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
