import "react-step-progress-bar/styles.css";
import { ProgressBar, Step } from "react-step-progress-bar";
import "./style.css";

const ProcessBarCustom = () => {
  const step1Content = <h1>Step 1</h1>;
  const step2Content = <h1>Step 2</h1>;
  const step3Content = <h1>Step 3</h1>;

  return (
    <div className="bar_wrapper">
      <ProgressBar
        percent={100}
        filledBackground="linear-gradient(to right, #fefb72, #f0bb31)"
      >
        <Step transition="scale">
          {({ accomplished }) => (
            <div
              className="personal_info"
              style={{ background: `${accomplished ? "blue" : "green"}` }}
            >
              Personal Info
            </div>
          )}
        </Step>
        <Step transition="scale">
          {({ accomplished }) => (
            <div
              className="personal_info"
              style={{ background: `${accomplished ? "blue" : "green"}` }}
            >
              Personal Info
            </div>
          )}
        </Step>
        <Step transition="scale">
          {({ accomplished }) => (
            <img
              style={{ filter: `grayscale(${accomplished ? 0 : 80}%)` }}
              width="30"
              src="https://vignette.wikia.nocookie.net/pkmnshuffle/images/9/97/Pikachu_%28Smiling%29.png/revision/latest?cb=20170410234508"
            />
          )}
        </Step>
        <Step transition="scale">
          {({ accomplished }) => (
            <img
              style={{ filter: `grayscale(${accomplished ? 0 : 80}%)` }}
              width="30"
              src="https://vignette.wikia.nocookie.net/pkmnshuffle/images/9/97/Pikachu_%28Smiling%29.png/revision/latest?cb=20170410234508"
            />
          )}
        </Step>
      </ProgressBar>
    </div>
  );
};

export default ProcessBarCustom;
