import React, { useRef, useState, useEffect } from "react";
import Header from "../../Components/header";
import useClickOutside from "../../Components/helper/useClickOutside";
import "./style.css";

export default function Item() {
  const mainUrl =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_1_450x.png?v=1609399094";
  const subUrl0 = mainUrl;
  const subUrl1 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_06_450x.jpg?v=1609399094";
  const subUrl2 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_03_450x.jpg?v=1609399094";
  const subUrl3 =
    "https://cdn.shopify.com/s/files/1/0341/3498/2789/products/3348901250146_04_450x.jpg?v=1609399094";

  const [mainImg, setMainImg] = useState(mainUrl);
  const [optionVisible, setOptionVisible] = useState(false);
  const [navOption, setNavOption] = useState(0);

  const images = [subUrl0, subUrl1, subUrl2, subUrl3];

  const price0 = {
    ml: 100,
    price: 990,
  };
  const prices = [
    {
      ml: 150,
      price: 1499,
    },
    {
      ml: 200,
      price: 1799,
    },
    {
      ml: 250,
      price: 2099,
    },
  ];
  const [mainPrice, setMainPrice] = useState(price0);
  const [subPrice, setSubPrice] = useState(prices);

  const dropOption = useRef(null);

  useClickOutside(dropOption, () => {
    setOptionVisible(false);
  });

  return (
    <div>
      <Header />
      <div className="row">
        <div className="col_md">
          <div className="product_images">
            <img
              className="product_image  product-single__thumbnail-image"
              src={mainImg}
              alt="main image"
            />
          </div>
          <div className="other_images">
            {images.map((img, i) => (
              <img
                key={i}
                src={img}
                alt=""
                style={{ cursor: "pointer" }}
                onClick={() => {
                  setMainImg(img);
                }}
              />
            ))}
          </div>
        </div>

        <div className="col_md" style={{ "padding-top": "2em" }}>
          <div className="product_header">
            <h1>Sauvage</h1>
            <h2>Eau de toilette</h2>
          </div>
          <div className="form" ref={dropOption}>
            <div className="option_box hover1">
              <button
                onClick={() =>
                  !optionVisible
                    ? setOptionVisible(true)
                    : setOptionVisible(false)
                }
              >
                <div style={{ width: "100%" }}>
                  <span style={{ float: "left" }}>{mainPrice.ml} ml</span>
                  <span style={{ float: "right" }}>${mainPrice.price}</span>
                </div>
              </button>
            </div>

            {optionVisible && (
              <div className="other_option_box">
                {subPrice.map((item, i) => (
                  <div
                    className="option hover1"
                    style={{ width: "100%", padding: "10px" }}
                    onClick={() => {
                      const tmp = mainPrice;
                      setMainPrice(item);
                      setOptionVisible(false);
                      subPrice[i] = tmp;
                      setSubPrice(subPrice.sort());
                    }}
                  >
                    <span style={{ float: "left" }}>{item.ml} ml</span>
                    <span style={{ float: "right" }}>${item.price}</span>
                  </div>
                ))}
              </div>
            )}
            <div className="form_row2">
              <input
                className="control"
                type="number"
                value="1"
                min="1"
                max="5"
              />
              <button className="add_to_cart">
                <span>Add to Cart</span>
              </button>
            </div>
            <div className="delivery">
              <a href="">DELIVERY & RETURNS</a>
            </div>
          </div>
        </div>
        <div className="description_box">
          <div className="description nav_tab">
            <a>
              <h3>DESCRIPTION</h3>
            </a>
            <a>
              <h3>OLDFACTORY NOTES</h3>
            </a>
            <a>
              <h3>PERFUMER'S WORD</h3>
            </a>
            <a>
              <h3>OLDFACTORY DESCRIPTION</h3>
            </a>
            <a>
              <h3>KNOW HOW</h3>
            </a>
          </div>

          {navOption == 0 && (
            <div className="description_content">
              <p>
                Sauvage is an act of creation inspired by wide-open spaces. An
                ozone blue sky that dominates a white-hot rocky landscape. A
                bold composition for a true-to-himself man.
                <br />
                <br />
                "To create Sauvage, I used man as my starting point. A strong
                and unmistakable masculinity. Like the image of a man who
                transcends time and fashion."
                <br />
                <br />
                François Demachy, Dior Perfumer-Creator
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
